import{_ as t,o as a,c as e,Q as o}from"./chunks/framework.60fe54dc.js";const f=JSON.parse('{"title":"明记肠粉","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"food/小南门/明记肠粉.md","filePath":"food/小南门/明记肠粉.md","lastUpdated":1696684299000}'),s={name:"food/小南门/明记肠粉.md"},p=o('<h1 id="明记肠粉" tabindex="-1">明记肠粉 <a class="header-anchor" href="#明记肠粉" aria-label="Permalink to &quot;明记肠粉&quot;">​</a></h1><h2 id="位置" tabindex="-1">🔍位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag:位置&quot;">​</a></h2><p>店面就在小南门，很好找，店里有几张小桌子。</p><img src="https://s2.loli.net/2023/09/22/1VPp7J5ajhYEbof.png" alt="image-20230914230515424" style="zoom:50%;"><h2 id="🌰食评" tabindex="-1">🌰食评 <a class="header-anchor" href="#🌰食评" aria-label="Permalink to &quot;🌰食评&quot;">​</a></h2><p>老板娘人很好，真的有在用心研究菜品，每天也会在群里发点小红包福利。</p><p>最推荐的是肠粉，外卖也能点的到，属于是广东人认证合格的肠粉，肠粉的酱汁也不错，鲜虾肠粉据说是每天早上起大早现选的鲜虾然后剥的。</p><p>分量就是普通肠粉的分量（也就是如果当午饭一份的话可能吃不饱）。</p><p>最近新上了粿条（可以理解为是广东潮汕地区特有的一种米粉），肉放的挺多的，猪肝稍微有一点点韧，不够嫩，粿条的汤喝起来有一种“面”的感觉，不太知道汤具体放了什么，感觉没有骨头汤那种清甜浓郁的鲜香，汤的口味比较偏北方一点，另外就是粿条调味上真的非常非常的淡，连我一个广东人尝起来都觉得有点太淡了，不过桌上有调料瓶可以看口味自己调，最后就是粿条里面的炸蒜总觉得没有起到提香的作用。</p><p>有些时候会有卤味（还没尝，尝了补上）。</p><p><img src="https://s2.loli.net/2023/09/14/HWpLf9BcZRYDsCA.png" alt="image-20230914230614398"></p><p><img src="https://s2.loli.net/2023/09/14/nYy1sgkSWBUPh6f.png" alt="image-20230914230620122"></p><p><img src="https://s2.loli.net/2023/09/14/nYy1sgkSWBUPh6f.png" alt=""></p><p><img src="https://s2.loli.net/2023/09/14/JTEBCVfPUzoD54W.png" alt="image-20230914230632343"></p>',14),r=[p];function i(l,n,c,_,h,d){return a(),e("div",null,r)}const g=t(s,[["render",i]]);export{f as __pageData,g as default};
