import{_ as a,o as t,c as e,Q as o}from"./chunks/framework.adf0a5a1.js";const z=JSON.parse('{"title":"巨鲸pizza","description":"","frontmatter":{"author":"Clo0oOoud"},"headers":[],"relativePath":"food/泉山区/巨鲸pizza.md","filePath":"food/泉山区/巨鲸pizza.md","lastUpdated":1696684299000}'),i={name:"food/泉山区/巨鲸pizza.md"},p=o('<h1 id="巨鲸pizza" tabindex="-1">巨鲸pizza <a class="header-anchor" href="#巨鲸pizza" aria-label="Permalink to &quot;巨鲸pizza&quot;">​</a></h1><h2 id="位置" tabindex="-1">🔍位置 <a class="header-anchor" href="#位置" aria-label="Permalink to &quot;:mag:位置&quot;">​</a></h2><img src="https://s2.loli.net/2023/09/11/tBz8irMF4PqxyN7.png" alt="image-20230911233345919" style="zoom:50%;"><p>在富国街老地方饭店背面的一个小巷子里边，打车定位老地方饭店就可以。</p><h2 id="🌰食评" tabindex="-1">🌰食评 <a class="header-anchor" href="#🌰食评" aria-label="Permalink to &quot;🌰食评&quot;">​</a></h2><p>小食、沙拉和汉堡很好吃，汉堡的分量很足，小食推荐粗薯，之前有香肠拼盘，但是近几次去好像没有了，如果菜单上有的话香肠拼盘也推荐尝一尝。建议第一次去的话可以不点披萨，只点汉堡和小食以及沙拉，因为他家披萨比起其它店不算很出彩，中规中矩，double cheese的披萨甚至有一点腻。店内的饮料选择不多，味道也一般。</p><p>他家通常都需要等位，虽然扩店了，但也火起来了。所以用餐高峰期的时候建议早一点去。</p><p>大众点评上可以搜到他们。</p><p>综合来说四星推荐。建议3到4个人去吃。</p><p><img src="https://s2.loli.net/2023/09/11/FJG2XBPNSZ9tLfh.png" alt="image-20230911233526335"></p><p><img src="https://s2.loli.net/2023/09/11/yYQ5cdgOisjCaEe.png" alt="image-20230911233533241"></p><p><img src="https://s2.loli.net/2023/09/11/lRzeX9r5gm8op7y.png" alt="image-20230911233539311"></p><p><img src="https://s2.loli.net/2023/09/11/xuPCHaz1YilQF86.png" alt="image-20230911233545543"></p>',13),s=[p];function l(r,n,c,d,h,_){return t(),e("div",null,s)}const g=a(i,[["render",l]]);export{z as __pageData,g as default};
